document.getElementById("name").addEventListener("blur",validateName),document.getElementById("email").addEventListener("blur",validateEmail),document.getElementById("phone").addEventListener("blur",validatePhone),document.getElementById("sendMessageButton").addEventListener("click",validateForm);const btnSend=document.getElementById("sendMessageButton");function validateName(){const name=document.getElementById("name"),re=/^[a-zA-Z]{2,10}$/;re.test(name.value)?name.classList.remove("is-invalid"):name.classList.add("is-invalid"),validateForm(name)}function validateEmail(){const email=document.getElementById("email"),re=/^([a-zA-Z0-9_\-\.]+)@([a-zA-Z0-9_\-\.]+)\.([a-zA-Z]{2,5})$/;re.test(email.value)?email.classList.remove("is-invalid"):email.classList.add("is-invalid"),validateForm(email)}function validatePhone(){const phone=document.getElementById("phone"),re=/^\(?\d{3}\)?[-. ]?\d{3}[-. ]?\d{4}$/;re.test(phone.value)?phone.classList.remove("is-invalid"):phone.classList.add("is-invalid"),validateForm(phone)}function validateForm(el){el.classList.contains("is-invalid")?btnSend.disabled=!0:btnSend.disabled=!1}btnSend.disabled=!0;